  def operator(input: List[List[String]], moves: List[List[Int]]) = {

    moves.foldLeft(input) {
      (acc, curr) =>
        val (take, from, to) = (curr.head, curr(1), curr.last)

        acc.updated(to - 1, acc(from - 1).take(take) ::: acc(to - 1)).updated(from - 1, acc(from - 1).drop(take))

    }.map(_.head).mkString.filter(_.isLetter)
  }


